"""## **Parte Geral: Abertura de arquivos, criação do desenho do mapa, preenchimento da classe Point, e conversão de dados.**"""

class Point(object): #classe de abstração de um ponto
    def __init__(self, x, y, media):
        self.x = x
        self.y = y
        self.media = media
       
#pecorre o shapefile e retorna o DataFrame correspondente
def convert_to_DataFrame(sf): 
    fields = [x[0] for x in sf.fields][1:]
    records = sf.records()
    shps = [s.points for s in sf.shapes()]    
    df = pd.DataFrame(columns=fields, data=records)
    df = df.assign(coords=shps)    
    return df
#função responsável por desenhar o mapa e cálcular as intensidades dos munícipios 
def create_map_shape_and_intensities(sf,figsize = (11,9)):  
    plt.figure(figsize=figsize)   
    id=0  
    list_of_points = [] #vetor auxiliar para armazenar as intensidades 
    for shape in sf.shapeRecords(): #pecorre os munícipios
        x = [i[0] for i in shape.shape.points[:]] #pega a longitude do munícipio
        y = [i[1] for i in shape.shape.points[:]] #pega a latitude do munícipio
        plt.plot(x, y, 'k') #linha de divisão dos estados
        
        x0 = np.mean(x) #retorna a média do vetor x
        y0 = np.mean(y) #retorna a média dos vetor y
        plt.text(x0, y0, shape.record[2], fontsize=10) #seta o nome de cada munícipio no mapa
        plt.scatter(x0, y0, s=50,c="r") #seta o ponto no mapa  
        #print(shape.record[1],"|","lon.: ",x0,'lat.:',y0) #imprime o nome do munícipio e suas coordenadas
        media = get_media(shape.record[2]) #seta a media 
        P = Point(x=x0,y=y0,media=media)  
        list_of_points.append(P) 
        id += 1 #usado para numerar os munícipios caso não queira usar os nomes 
    return np.array(list_of_points)   
    
def get_media(sheet_name): #função usada para calcular a média de um munícipio especifico (pelo nome da folha) 
    xls = xlrd.open_workbook("/content/drive/My Drive/PPC-Trabalho/Banco de dados/data(1).xls") #carrega os dados pluviométricos
    for nome_cidade in xls.sheet_names(): #pecorres todas as folhas 
        if(unidecode(nome_cidade.lower()) == unidecode(sheet_name.lower())): #checa se existe uma folha especifica
            folha = xls.sheet_by_name(nome_cidade) #pega a planilha relacionada com o index (os dados da folha) 
            for i in range(folha.nrows): #pecorre as linhas da folha 
                for j in range(folha.ncols):  #pecorre as colunas da folha  
                    if str(folha.cell(i,j).value).lower().strip() == "média mensal": #checa se a celula atualmente acessada chama-se "média mensal" 
                        sum_media = 0 #variável para auxiliar a soma
                        for media in folha.row_values(i,j+1,14): #pecorre e soma as médias
                            sum_media += media 
                        return sum_media/12 #retorna a média das médias
    
            
    return 0#retorna 0 se o munícipio não tiver dados pluviométricos  
        


#função de leitura do shapefile
def read_shp(file_name,file_encoding="ISO8859-1"):
    sf = shp.Reader(file_name, encoding=file_encoding)
    df = convert_to_DataFrame(sf)  
    return sf, df #retorna o shape file e o DataFrame equivalente

"""## **Funções relacionadas a CPU**"""

#Interpolar linearmente os dados em uma grade definida por (grid_x, grid_y).
def triangulation_linearInterpolation(lon,lat,grid_x,grid_y,intensity):
    triang = tri.Triangulation(lon, lat) 
    #realiza a triangulação no escopo do mapa com o vetor de intensidades 
    interpolator = tri.LinearTriInterpolator(triang, intensity[:,0])  

    #vetoriza as grides
    Xi, Yi = np.meshgrid(grid_x, grid_y) 
    #retorna interpolação das grides vetorizadas 
    return interpolator(Xi, Yi)    
 
#interpola sequencialmente utlizando interpolação pelo vizinho mais próximo       
def VMP(list_points):  
    intensity_list = [] 
    for current in list_points: #a lista de pontos
        #print("x:",current.x,"y:",current.y,"Media:",current.media)  
        distancies = []
        menorDistancia = 0
        average = current.media
        for target in list_points:
            d = math.sqrt((target.x - current.x)**2 + (target.y - current.y)**2)
            #print("d((",current.x,",",current.y,"),(",target.x,",",target.y,")) =",d)
            if(menorDistancia > d or (menorDistancia == 0 and target != current)):
                menorDistancia = d
                average = target.media
                   
        intensity_list.append(average) #distancies[np.where(np.array(distancies) == min(np.array(distancies)[:,0]))[0][0]][1].media
        
    return np.array(intensity_list) #retorna a lista agrupada    

def triangulation_Interpolation(lon,lat,list_points):
    inicio_seq = time.time()
    triang = tri.Triangulation(lon,lat) 
    interpoled_intensity = VMP(list_points) #seta a intensidade interpolada pelos pontos vizinhos  
    fim_seq = time.time()
    tempo_seq = fim_seq - inicio_seq
    print("Tempo Seq:",tempo_seq)    
    return tempo_seq, triang, interpoled_intensity

"""## **Funcções relacionadas a GPU**"""

def interpolacaoKernelCUDA():
    #Kernel para realizar a interpolação por vizinhos mais próximos 
    mod = SourceModule(""" 
       __global__ void VMP(float *lon, float *lat, float *intensity) {
            int idx =  threadIdx.x + blockIdx.x * blockDim.x;
            float menorDistancia = 0;
            float average = intensity[idx];

            for(int j=0;j<75;j++){
               float d = sqrt(pow((lon[j] - lon[idx]),2) + pow((lat[j] - lat[idx]),2)); 
               
               if(menorDistancia > d || (menorDistancia == 0 && j!=idx)){
                 menorDistancia = d;
                 average = intensity[j];
               } 
            }
            intensity[idx] = average; 
       }
   """)  
    return mod.get_function("VMP")  

#Funcao para calcular a intensidade interpolada pelos pontos vizinhos na GPU    
def triangulation_InterpolationGPU(lon, lat,list_of_points):
    
    pointsX = []
    pointsY = []
    intensity = []

    #Acessando coordenadas geograficas e intensidades
    for point in list_of_points:
        pointsX.append(point.x)
        pointsY.append(point.y)
        intensity.append(point.media)
         
    pointsXArray = np.array(pointsX).astype(np.float32) 
    pointsYArray = np.array(pointsY).astype(np.float32) 
    intensityArray = np.array(intensity).astype(np.float32) 

    #Alocando em GPU os vetores: Longitude, Latitude e Intensidades...                         
    lon_gpu = cuda.mem_alloc(pointsXArray.nbytes)
    cuda.memcpy_htod(lon_gpu, pointsXArray) 
    lat_gpu = cuda.mem_alloc(pointsYArray.nbytes)
    cuda.memcpy_htod(lat_gpu, pointsYArray) 
    intensity_gpu = cuda.mem_alloc(intensityArray.nbytes)
    cuda.memcpy_htod(intensity_gpu, intensityArray) 
  
    intensityGPU = np.empty_like(intensityArray)  

    #Definindo Kernel da execução em GPU e organização da memória
    VMP = interpolacaoKernelCUDA()
    inicio_parallel = time.time()
    VMP(lon_gpu,lat_gpu,intensity_gpu,block=(75,1,1)); 
    fim_parallel = time.time()
    tempo_parallel = fim_parallel - inicio_parallel
    print("Tempo Paralelo:",tempo_parallel)
    #Recebendo vetor de intensidades interpolada na GPU
    cuda.memcpy_dtoh(intensityGPU, intensity_gpu) 
 

    return tempo_parallel, tri.Triangulation(lon,lat), intensityGPU

